              </div>
            </Row>
            <Row className="Seller">
              <Image
                className="seller-image"
                src={require("../assets/Screen Shot 2020-05-28 at 5.50.34 PM.png")}
              ></Image>
              <p>+91 **********</p>
              <Button>Contact Seller</Button>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ItemDetail;
