import React, { useState, useEffect, Component } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import Header from "../component/header";
import "./Form1.scss";
import CardCategory from "../component/CardCategory";
import { connect } from "react-redux";

const Form1  = ()=> {

  let handleChange = (e) => {
    this.props.dispatch({
      type:"UPDATE_PRODUCT_NAME",
      payload:{
        "value":e.target.value        
      }
    });
  };

    let history = useHistory();
    return (
      <div className="form1">
        <Container fluid>
          {/* <Row>
          <Header />
        </Row> */}
          <Row className="content">
            {/* <h3>Enter Product Detail</h3>
          <hr/> */}
            <Col md={{ span: 6, offset: 3 }}>
              <Form>
                <Form.Group controlId="exampleForm.ControlInput1">
                  {/* <Form.Label>Product Name</Form.Label> */}
                  <Form.Control
                    type="input"
                    placeholder="Enter the product name here"
                    onChange={(e) => {
                      this.handleChange(e);
                    }}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput1">
                  {/* <Form.Label>Brand</Form.Label> */}
                  <Form.Control
                    type="input"
                    placeholder="Enter the brand name here"
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput1">
                  {/* <Form.Label>How old?</Form.Label> */}
                  <Form.Control
                    type="input"
                    placeholder="How old the product is?"
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput1">
                  {/* <Form.Label>Description</Form.Label> */}
                  <Form.Control
                    type="input"
                    placeholder="Write the description about the product"
                  />
                </Form.Group>
              </Form>
              <Button
                onClick={() => {
                  history.push("/sellerForm/2");
                }}
              >
                Save&Continue
              </Button>
            </Col>
          </Row>
        </Container>
      </div>
    );

}

export default connect(mapStateToProps)(Form1);
