        imgExtension={['.jpeg','.jpg', '.gif', '.png', '.gif']}
        maxFileSize={5242880}
    />
    {/* <Button onClick = {()=>{history.push('/sellerForm/3')}} >Save&Continue</Button> */}
    </div>
);
};

export default Form2;
